        /**
         * @since 7.3
         */
        public const SQLITE_OPEN_READWRITE = 2;

        /**
         * @since 7.3
         */
        public const SQLITE_OPEN_CREATE = 4;

        /**
         * @since 7.3
         */
        public const SQLITE_ATTR_OPEN_FLAGS = 1000;

