style="color: #9876AA">&lt;?php<br></span><span style="color: #FF8000">// This iterator filters all values with less than 10 characters<br></span><span style="color: #007700">class </span><span style="color: #9876AA">LengthFilterIterator </span><span style="color: #007700">extends </span><span style="color: #9876AA">FilterIterator </span><span style="color: #007700">{<br><br>    public function </span><span style="color: #9876AA">accept</span><span style="color: #007700">() {<br>        </span><span style="color: #FF8000">// Only accept strings with a length of 10 and greater<br>        </span><span style="color: #007700">return </span><span style="color: #9876AA">strlen</span><span style="color: #007700">(</span><span style="color: #9876AA">parent</span><span style="color: #007700">::</span><span style="color: #9876AA">current</span><span style="color: #007700">()) &gt;= </span><span style="color: #9876AA">10</span><span style="color: #007700">;<br>    }<br><br>}<br><br></span><span style="color: #9876AA">\$arrayIterator </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayIterator</span><span style="color: #007700">(array(</span><span style="color: #DD0000">'test1'</span><span style="color: #007700">, </span><span style="color: #DD0000">'more than 10 characters'</span><span style="color: #007700">));<br></span><span style="color: #9876AA">\$lengthFilter </span><span style="color: #007700">= new </span><span style="color: #9876AA">LengthFilterIterator</span><span style="color: #007700">(</span><span style="color: #9876AA">\$arrayIterator</span><span style="color: #007700">);<br><br>foreach (</span><span style="color: #9876AA">\$lengthFilter </span><span style="color: #007700">as </span><span style="color: #9876AA">\$value</span><span style="color: #007700">) {<br>    echo </span><span style="color: #9876AA">\$value </span><span style="color: #007700">. </span><span style="color: #DD0000">"\n"</span><span style="color: #007700">;<br>}<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">more&nbsp;than&nbsp;10&nbsp;characters</blockquote></div>    </div>   </div>   </div></div>
