    /**
 * <div id="arrayobject.offsetset" class="refentry"> <div class="refnamediv">  <h1 class="refname">ArrayObject::offsetSet</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">ArrayObject::offsetSet</span> — <span class="dc-title">为指定索引设定新值</span></p> </div> <div class="refsect1 description" id="refsect1-arrayobject.offsetset-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>ArrayObject::offsetSet</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.mixed.php" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">\$key</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.mixed.php" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">\$value</span></span>): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.void.php" class="type void" style="color:#EAB766">void</a></span></div>  <p class="para rdfs-comment">   设置指定的索引为新值。  </p> </div> <div class="refsect1 parameters" id="refsect1-arrayobject.offsetset-parameters">  <h3 class="title">参数</h3>  <p class="para">   </p><dl>         <dt><span class="parameter" style="color:#3A95FF">key</span></dt>     <dd>      <p class="para">       将要被设置的索引。      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">value</span></dt>     <dd>      <p class="para">       参数 <span class="parameter" style="color:#3A95FF">key</span> 所对应的新值。      </p>     </dd>       </dl>   </div> <div class="refsect1 returnvalues" id="refsect1-arrayobject.offsetset-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   没有返回值。  </p> </div> <div class="refsect1 examples" id="refsect1-arrayobject.offsetset-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-3844">    <p><strong>示例 #1 <span class="methodname" style="color:#CC7832"><strong>ArrayObject::offsetSet()</strong></span> 例子</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br></span><span style="color: #007700">class </span><span style="color: #9876AA">Example </span><span style="color: #007700">{<br>    public </span><span style="color: #9876AA">\$property </span><span style="color: #007700">= </span><span style="color: #DD0000">'prop:public'</span><span style="color: #007700">;<br>}<br></span><span style="color: #9876AA">\$arrayobj </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(new </span><span style="color: #9876AA">Example</span><span style="color: #007700">());<br></span><span style="color: #9876AA">\$arrayobj</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">offsetSet</span><span style="color: #007700">(</span><span style="color: #9876AA">4</span><span style="color: #007700">, </span><span style="color: #DD0000">'four'</span><span style="color: #007700">);<br></span><span style="color: #9876AA">\$arrayobj</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">offsetSet</span><span style="color: #007700">(</span><span style="color: #DD0000">'group'</span><span style="color: #007700">, array(</span><span style="color: #DD0000">'g1'</span><span style="color: #007700">, </span><span style="color: #DD0000">'g2'</span><span style="color: #007700">));<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$arrayobj</span><span style="color: #007700">);<br><br></span><span style="color: #9876AA">\$arrayobj </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(array(</span><span style="color: #DD0000">'zero'</span><span style="color: #007700">,</span><span style="color: #DD0000">'one'</span><span style="color: #007700">));<br></span><span style="color: #9876AA">\$arrayobj</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">offsetSet</span><span style="color: #007700">(</span><span style="color: #9876AA">null</span><span style="color: #007700">, </span><span style="color: #DD0000">'last'</span><span style="color: #007700">);<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$arrayobj</span><span style="color: #007700">);<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">object(ArrayObject)#1&nbsp;(1)&nbsp;{<br>&nbsp;&nbsp;["storage":"ArrayObject":private]=&gt;<br>&nbsp;&nbsp;object(Example)#2&nbsp;(3)&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;["property"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(11)&nbsp;"prop:public"<br>&nbsp;&nbsp;&nbsp;&nbsp;["4"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(4)&nbsp;"four"<br>&nbsp;&nbsp;&nbsp;&nbsp;["group"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;array(2)&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;[0]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;string(2)&nbsp;"g1"<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;[1]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;string(2)&nbsp;"g2"<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;}<br>}<br>object(ArrayObject)#3&nbsp;(1)&nbsp;{<br>&nbsp;&nbsp;["storage":"ArrayObject":private]=&gt;<br>&nbsp;&nbsp;array(3)&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;[0]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(4)&nbsp;"zero"<br>&nbsp;&nbsp;&nbsp;&nbsp;[1]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(3)&nbsp;"one"<br>&nbsp;&nbsp;&nbsp;&nbsp;[2]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(4)&nbsp;"last"<br>&nbsp;&nbsp;}<br>}</blockquote></div>    </div>   </div>   </div>  <div class="refsect1 seealso" id="refsect1-arrayobject.offsetset-seealso">  <h3 class="title">参见</h3>  <p class="para">   </p><ul class="simplelist">    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::append()} - 追加新的值作为最后一个元素。</span></li>   </ul>   </div></div>
