    /**
 * <div id="arrayobject.append" class="refentry"> <div class="refnamediv">  <h1 class="refname">ArrayObject::append</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">ArrayObject::append</span> — <span class="dc-title">追加新的值作为最后一个元素。</span></p> </div> <div class="refsect1 description" id="refsect1-arrayobject.append-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>ArrayObject::append</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.mixed.php" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">\$value</span></span>): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.void.php" class="type void" style="color:#EAB766">void</a></span></div>  <p class="para rdfs-comment">   追加新的值作为最后一个元素。  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    </p><p class="para">    当 <span class="classname"><a href="https://php.net/manual/zh/class.arrayobject.php" class="classname">ArrayObject</a></span> 从 object 初始化时不能调用此方法。使用 <span class="methodname" style="color:#CC7832">{@link ArrayObject::offsetSet()}</span> 代替。   </p>  </blockquote> </div> <div class="refsect1 parameters" id="refsect1-arrayobject.append-parameters">  <h3 class="title">参数</h3>  <p class="para">   </p><dl>         <dt><span class="parameter" style="color:#3A95FF">value</span></dt>     <dd>      <p class="para">       将要被追加的值      </p>     </dd>       </dl>   </div> <div class="refsect1 returnvalues" id="refsect1-arrayobject.append-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   没有返回值。  </p> </div><div class="refsect1 examples" id="refsect1-arrayobject.append-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-3830">    <p><strong>示例 #1 <span class="methodname" style="color:#CC7832"><strong>ArrayObject::append()</strong></span> 例子</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br>\$arrayobj </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(array(</span><span style="color: #DD0000">'first'</span><span style="color: #007700">,</span><span style="color: #DD0000">'second'</span><span style="color: #007700">,</span><span style="color: #DD0000">'third'</span><span style="color: #007700">));<br></span><span style="color: #9876AA">\$arrayobj</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">append</span><span style="color: #007700">(</span><span style="color: #DD0000">'fourth'</span><span style="color: #007700">);<br></span><span style="color: #9876AA">\$arrayobj</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">append</span><span style="color: #007700">(array(</span><span style="color: #DD0000">'five'</span><span style="color: #007700">, </span><span style="color: #DD0000">'six'</span><span style="color: #007700">));<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$arrayobj</span><span style="color: #007700">);<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">object(ArrayObject)#1&nbsp;(1)&nbsp;{<br>&nbsp;&nbsp;["storage":"ArrayObject":private]=&gt;<br>&nbsp;&nbsp;array(5)&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;[0]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(5)&nbsp;"first"<br>&nbsp;&nbsp;&nbsp;&nbsp;[1]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(6)&nbsp;"second"<br>&nbsp;&nbsp;&nbsp;&nbsp;[2]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(5)&nbsp;"third"<br>&nbsp;&nbsp;&nbsp;&nbsp;[3]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(6)&nbsp;"fourth"<br>&nbsp;&nbsp;&nbsp;&nbsp;[4]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;array(2)&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;[0]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;string(4)&nbsp;"five"<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;[1]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;string(3)&nbsp;"six"<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;}<br>}</blockquote></div>    </div>   </div>   </div> <div class="refsect1 seealso" id="refsect1-arrayobject.append-seealso">  <h3 class="title">参见</h3>  <p class="para">   </p><ul class="simplelist">    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::offsetSet()} - 为指定索引设定新值</span></li>   </ul>   </div></div>
 * 
     * Appends the value
     * @link https://php.net/manual/zh/arrayobject.append.php
     * @param TValue $value <p>
     * The value being appended.
     * </p>
     * @return void
     */
    #[TentativeType]
    public function append(#[LanguageLevelTypeAware(['8.0' => 'mixed'], default: '')] $value): void {}

    /**
 * <div id="arrayobject.getarraycopy" class="refentry"> <div class="refnamediv">  <h1 class="refname">ArrayObject::getArrayCopy</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">ArrayObject::getArrayCopy</span> — <span class="dc-title">Creates a copy of the ArrayObject</span></p> </div> <div class="refsect1 description" id="refsect1-arrayobject.getarraycopy-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>ArrayObject::getArrayCopy</strong></span>(): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.array.php" class="type array" style="color:#EAB766">array</a></span></div>  <p class="para rdfs-comment">   Exports the <span class="classname"><a href="https://php.net/manual/zh/class.arrayobject.php" class="classname">ArrayObject</a></span> to an array.  </p> </div> <div class="refsect1 parameters" id="refsect1-arrayobject.getarraycopy-parameters">  <h3 class="title">参数</h3>  <p class="para">此函数没有参数。</p> </div> <div class="refsect1 returnvalues" id="refsect1-arrayobject.getarraycopy-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns a copy of the array. When the <span class="classname"><a href="https://php.net/manual/zh/class.arrayobject.php" class="classname">ArrayObject</a></span> refers to an object,   an array of the properties of that object will be returned.  </p> </div> <div class="refsect1 examples" id="refsect1-arrayobject.getarraycopy-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-3835">    <p><strong>示例 #1 <span class="function"><strong>ArrayObject::getArrayCopy()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br></span><span style="color: #FF8000">// Array of available fruits<br></span><span style="color: #9876AA">\$fruits </span><span style="color: #007700">= array(</span><span style="color: #DD0000">"lemons" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #DD0000">"oranges" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">4</span><span style="color: #007700">, </span><span style="color: #DD0000">"bananas" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">5</span><span style="color: #007700">, </span><span style="color: #DD0000">"apples" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">10</span><span style="color: #007700">);<br><br></span><span style="color: #9876AA">\$fruitsArrayObject </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(</span><span style="color: #9876AA">\$fruits</span><span style="color: #007700">);<br></span><span style="color: #9876AA">\$fruitsArrayObject</span><span style="color: #007700">[</span><span style="color: #DD0000">'pears'</span><span style="color: #007700">] = </span><span style="color: #9876AA">4</span><span style="color: #007700">;<br><br></span><span style="color: #FF8000">// create a copy of the array<br></span><span style="color: #9876AA">\$copy </span><span style="color: #007700">= </span><span style="color: #9876AA">\$fruitsArrayObject</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getArrayCopy</span><span style="color: #007700">();<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$copy</span><span style="color: #007700">);<br><br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">array(5)&nbsp;{<br>&nbsp;&nbsp;["lemons"]=&gt;<br>&nbsp;&nbsp;int(1)<br>&nbsp;&nbsp;["oranges"]=&gt;<br>&nbsp;&nbsp;int(4)<br>&nbsp;&nbsp;["bananas"]=&gt;<br>&nbsp;&nbsp;int(5)<br>&nbsp;&nbsp;["apples"]=&gt;<br>&nbsp;&nbsp;int(10)<br>&nbsp;&nbsp;["pears"]=&gt;<br>&nbsp;&nbsp;int(4)<br>}</blockquote></div>    </div>   </div>   </div></div>
 * 
     * Creates a copy of the ArrayObject.
     * @link https://php.net/manual/zh/arrayobject.getarraycopy.php
     * @return array<TValue> a copy of the array. When the <b>ArrayObject</b> refers to an object
     * an array of the public properties of that object will be returned.
     */
    #[TentativeType]
    public function getArrayCopy(): array {}

    /**
 * <div id="arrayobject.count" class="refentry"> <div class="refnamediv">  <h1 class="refname">ArrayObject::count</h1>  <p class="verinfo">(PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">ArrayObject::count</span> — <span class="dc-title">统计 ArrayObject 内 public 属性的数量</span></p> </div> <div class="refsect1 description" id="refsect1-arrayobject.count-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>ArrayObject::count</strong></span>(): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.integer.php" class="type int" style="color:#EAB766">int</a></span></div>  <p class="para rdfs-comment">   获取 <span class="classname"><a href="https://php.net/manual/zh/class.arrayobject.php" class="classname">ArrayObject</a></span> 的 public 属性数量  </p> </div> <div class="refsect1 parameters" id="refsect1-arrayobject.count-parameters">  <h3 class="title">参数</h3>  <p class="para">此函数没有参数。</p> </div> <div class="refsect1 returnvalues" id="refsect1-arrayobject.count-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   对象 <span class="classname"><a href="https://php.net/manual/zh/class.arrayobject.php" class="classname">ArrayObject</a></span> 的 public 属性数量  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:    </p><p class="para">    当对象 <span class="classname"><a href="https://php.net/manual/zh/class.arrayobject.php" class="classname">ArrayObject</a></span> 是从数组构造而来时，所有属性都是 public 的。   </p>  </blockquote> </div> <div class="refsect1 examples" id="refsect1-arrayobject.count-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-3833">    <p><strong>示例 #1 <span class="methodname" style="color:#CC7832"><strong>ArrayObject::count()</strong></span> 例子</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br></span><span style="color: #007700">class </span><span style="color: #9876AA">Example </span><span style="color: #007700">{<br>    public </span><span style="color: #9876AA">\$public </span><span style="color: #007700">= </span><span style="color: #DD0000">'prop:public'</span><span style="color: #007700">;<br>    private </span><span style="color: #9876AA">\$prv   </span><span style="color: #007700">= </span><span style="color: #DD0000">'prop:private'</span><span style="color: #007700">;<br>    protected </span><span style="color: #9876AA">\$prt </span><span style="color: #007700">= </span><span style="color: #DD0000">'prop:protected'</span><span style="color: #007700">;<br>}<br><br></span><span style="color: #9876AA">\$arrayobj </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(new </span><span style="color: #9876AA">Example</span><span style="color: #007700">());<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$arrayobj</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">count</span><span style="color: #007700">());<br><br></span><span style="color: #9876AA">\$arrayobj </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(array(</span><span style="color: #DD0000">'first'</span><span style="color: #007700">,</span><span style="color: #DD0000">'second'</span><span style="color: #007700">,</span><span style="color: #DD0000">'third'</span><span style="color: #007700">));<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$arrayobj</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">count</span><span style="color: #007700">());<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">int(1)<br>int(3)</blockquote></div>    </div>   </div>   </div> </div>
 * 
     * Get the number of public properties in the ArrayObject
     * When the <b>ArrayObject</b> is constructed from an array all properties are public.
     * @link https://php.net/manual/zh/arrayobject.count.php
     * @return int The number of public properties in the ArrayObject.
     */
    #[TentativeType]
    public function count(): int {}

    /**
 * <div id="arrayobject.getflags" class="refentry"> <div class="refnamediv">  <h1 class="refname">ArrayObject::getFlags</h1>  <p class="verinfo">(PHP 5 &gt;= 5.1.0, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">ArrayObject::getFlags</span> — <span class="dc-title">Gets the behavior flags</span></p> </div> <div class="refsect1 description" id="refsect1-arrayobject.getflags-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>ArrayObject::getFlags</strong></span>(): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.integer.php" class="type int" style="color:#EAB766">int</a></span></div>  <p class="para rdfs-comment">   Gets the behavior flags of the <span class="classname"><a href="https://php.net/manual/zh/class.arrayobject.php" class="classname">ArrayObject</a></span>. See the   {@link ArrayObject::setFlags}   method for a list of the available flags.  </p> </div> <div class="refsect1 parameters" id="refsect1-arrayobject.getflags-parameters">  <h3 class="title">参数</h3>  <p class="para">此函数没有参数。</p> </div> <div class="refsect1 returnvalues" id="refsect1-arrayobject.getflags-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns the behavior flags of the ArrayObject.  </p> </div> <div class="refsect1 examples" id="refsect1-arrayobject.getflags-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-3836">    <p><strong>示例 #1 <span class="function"><strong>ArrayObject::getFlags()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br></span><span style="color: #FF8000">// Array of available fruits<br></span><span style="color: #9876AA">\$fruits </span><span style="color: #007700">= array(</span><span style="color: #DD0000">"lemons" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #DD0000">"oranges" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">4</span><span style="color: #007700">, </span><span style="color: #DD0000">"bananas" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">5</span><span style="color: #007700">, </span><span style="color: #DD0000">"apples" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">10</span><span style="color: #007700">);<br><br></span><span style="color: #9876AA">\$fruitsArrayObject </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(</span><span style="color: #9876AA">\$fruits</span><span style="color: #007700">);<br><br></span><span style="color: #FF8000">// Get the current flags<br></span><span style="color: #9876AA">\$flags </span><span style="color: #007700">= </span><span style="color: #9876AA">\$fruitsArrayObject</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getFlags</span><span style="color: #007700">();<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$flags</span><span style="color: #007700">);<br><br></span><span style="color: #FF8000">// Set new flags<br></span><span style="color: #9876AA">\$fruitsArrayObject</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">setFlags</span><span style="color: #007700">(</span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">::</span><span style="color: #9876AA">ARRAY_AS_PROPS</span><span style="color: #007700">);<br><br></span><span style="color: #FF8000">// Get the new flags<br></span><span style="color: #9876AA">\$flags </span><span style="color: #007700">= </span><span style="color: #9876AA">\$fruitsArrayObject</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">getFlags</span><span style="color: #007700">();<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$flags</span><span style="color: #007700">);<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">int(0)<br>int(2)</blockquote></div>    </div>   </div>   </div> <div class="refsect1 seealso" id="refsect1-arrayobject.getflags-seealso">  <h3 class="title">参见</h3>  <p class="para">   </p><ul class="simplelist">    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::setFlags()} - Sets the behavior flags</span></li>   </ul>   </div></div>
 * 
     * Gets the behavior flags.
     * @link https://php.net/manual/zh/arrayobject.getflags.php
     * @return int the behavior flags of the ArrayObject.
     */
    #[TentativeType]
    public function getFlags(): int {}

    /**
 * <div id="arrayobject.setflags" class="refentry"> <div class="refnamediv">  <h1 class="refname">ArrayObject::setFlags</h1>  <p class="verinfo">(PHP 5 &gt;= 5.1.0, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">ArrayObject::setFlags</span> — <span class="dc-title">Sets the behavior flags</span></p> </div> <div class="refsect1 description" id="refsect1-arrayobject.setflags-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>ArrayObject::setFlags</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.integer.php" class="type int" style="color:#EAB766">int</a></span> <span class="parameter" style="color:#3A95FF">\$flags</span></span>): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.void.php" class="type void" style="color:#EAB766">void</a></span></div>  <p class="para rdfs-comment">   Set the flags that change the behavior of the ArrayObject.  </p> </div> <div class="refsect1 parameters" id="refsect1-arrayobject.setflags-parameters">  <h3 class="title">参数</h3>  <p class="para">   </p><dl>         <dt><span class="parameter" style="color:#3A95FF">flags</span></dt>     <dd>      <p class="para">       The new ArrayObject behavior.       It takes on either a bitmask, or named constants. Using named       constants is strongly encouraged to ensure compatibility for future       versions.      </p>      <p class="para">       The available behavior flags are listed below. The actual       meanings of these flags are described in the       <a href="https://php.net/manual/zh/class.arrayobject.php#arrayobject.constants" class="link">predefined constants</a>.       </p><table class="doctable table">        <caption><strong>ArrayObject behavior flags</strong></caption>                 <thead>          <tr>           <th>value</th>           <th>constant</th>          </tr>         </thead>         <tbody class="tbody">          <tr>           <td>1</td>           <td>            {@link ArrayObject::STD_PROP_LIST}           </td>          </tr>          <tr>           <td>2</td>           <td>            {@link ArrayObject::ARRAY_AS_PROPS}           </td>          </tr>         </tbody>               </table>           </dd>       </dl>   </div> <div class="refsect1 returnvalues" id="refsect1-arrayobject.setflags-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   没有返回值。  </p> </div> <div class="refsect1 examples" id="refsect1-arrayobject.setflags-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-3847">    <p><strong>示例 #1 <span class="function"><strong>ArrayObject::setFlags()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br></span><span style="color: #FF8000">// Array of available fruits<br></span><span style="color: #9876AA">\$fruits </span><span style="color: #007700">= array(</span><span style="color: #DD0000">"lemons" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #DD0000">"oranges" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">4</span><span style="color: #007700">, </span><span style="color: #DD0000">"bananas" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">5</span><span style="color: #007700">, </span><span style="color: #DD0000">"apples" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">10</span><span style="color: #007700">);<br><br></span><span style="color: #9876AA">\$fruitsArrayObject </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(</span><span style="color: #9876AA">\$fruits</span><span style="color: #007700">);<br><br></span><span style="color: #FF8000">// Try to use array key as property<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$fruitsArrayObject</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">lemons</span><span style="color: #007700">);<br></span><span style="color: #FF8000">// Set the flag so that the array keys can be used as properties of the ArrayObject<br></span><span style="color: #9876AA">\$fruitsArrayObject</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">setFlags</span><span style="color: #007700">(</span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">::</span><span style="color: #9876AA">ARRAY_AS_PROPS</span><span style="color: #007700">);<br></span><span style="color: #FF8000">// Try it again<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$fruitsArrayObject</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">lemons</span><span style="color: #007700">);<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例的输出类似于：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">Warning:&nbsp;Undefined&nbsp;property:&nbsp;ArrayObject::\$lemons&nbsp;in&nbsp;...<br>NULL<br>int(1)</blockquote></div>    </div>   </div>   </div></div>
 * 
     * Sets the behavior flags.
     * @link https://php.net/manual/zh/arrayobject.setflags.php
     * @param int $flags <p>
     * The new ArrayObject behavior.
     * It takes on either a bitmask, or named constants. Using named
     * constants is strongly encouraged to ensure compatibility for future
     * versions.
     * </p>
     * <p>
     * The available behavior flags are listed below. The actual
     * meanings of these flags are described in the
     * predefined constants.
     * <table>
     * ArrayObject behavior flags
     * <tr valign="top">
     * <td>value</td>
     * <td>constant</td>
     * </tr>
     * <tr valign="top">
     * <td>1</td>
     * <td>
     * ArrayObject::STD_PROP_LIST
     * </td>
     * </tr>
     * <tr valign="top">
     * <td>2</td>
     * <td>
     * ArrayObject::ARRAY_AS_PROPS
     * </td>
     * </tr>
     * </table>
     * </p>
     * @return void
     */
    #[TentativeType]
    public function setFlags(#[LanguageLevelTypeAware(['8.0' => 'int'], default: '')] $flags): void {}

    /**
 * <div id="arrayobject.asort" class="refentry"> <div class="refnamediv">  <h1 class="refname">ArrayObject::asort</h1>  <p class="verinfo">(PHP 5 &gt;= 5.2.0, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">ArrayObject::asort</span> — <span class="dc-title">Sort the entries by value</span></p> </div> <div class="refsect1 description" id="refsect1-arrayobject.asort-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>ArrayObject::asort</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.integer.php" class="type int" style="color:#EAB766">int</a></span> <span class="parameter" style="color:#3A95FF">\$flags</span><span class="initializer"> = <strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-regular">SORT_REGULAR</a></span></strong></span></span>): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.singleton.php" class="type true" style="color:#EAB766">true</a></span></div>  <p class="para rdfs-comment">   Sorts the entries in ascending order,   such that its keys maintain their correlation with the values they   are associated with.  </p>  <p class="para">   This is used mainly when sorting associative arrays where the actual   element order is significant.  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:  </p><p class="para">  如果两个成员完全相同，那么它们将保持原来的顺序。  在 PHP 8.0.0 之前，它们在排序数组中的相对顺序是未定义的。 </p></blockquote> </div> <div class="refsect1 parameters" id="refsect1-arrayobject.asort-parameters">  <h3 class="title">参数</h3>  <dl>     <dt><span class="parameter" style="color:#3A95FF">flags</span></dt>  <dd>  <p class="para">    可选的第二个参数 <span class="parameter" style="color:#3A95FF">flags</span>     可以用以下值改变排序的行为：  </p>  <p class="para">    排序类型标记：    </p><ul class="itemizedlist">    <li class="listitem">      <span class="simpara"><strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-regular">SORT_REGULAR</a></span></strong>  - 正常比较单元         详细描述参见 <a href="https://php.net/manual/zh/language.operators.comparison.php" class="link">比较运算符</a> 章节</span>    </li>    <li class="listitem">      <span class="simpara"><strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-numeric">SORT_NUMERIC</a></span></strong> - 单元被作为数字来比较</span>    </li>    <li class="listitem">      <span class="simpara"><strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-string">SORT_STRING</a></span></strong> - 单元被作为字符串来比较</span>    </li>    <li class="listitem">      <span class="simpara">      <strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-locale-string">SORT_LOCALE_STRING</a></span></strong> -      根据当前的区域（locale）设置来把单元当作字符串比较，可以用      <span class="function">{@link setlocale()}</span> 来改变。      </span>    </li>    <li class="listitem">      <span class="simpara">      <strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-natural">SORT_NATURAL</a></span></strong> - 和 <span class="function">{@link natsort()}</span>      类似对每个单元以“自然的顺序”对字符串进行排序。      </span>    </li>    <li class="listitem">      <span class="simpara">      <strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-flag-case">SORT_FLAG_CASE</a></span></strong> - 能够与      <strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-string">SORT_STRING</a></span></strong> 或         <strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-natural">SORT_NATURAL</a></span></strong>          合并（OR 位运算），不区分大小写排序字符串。      </span>    </li>    </ul>    </dd>  </dl> </div> <div class="refsect1 returnvalues" id="refsect1-arrayobject.asort-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   总是返回 <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.true">true</a></span></strong>。  </p> </div> <div class="refsect1 changelog" id="refsect1-arrayobject.asort-changelog">  <h3 class="title">更新日志</h3>  <p class="para">   </p><table class="doctable informaltable">         <thead>      <tr>       <th>版本</th>       <th>说明</th>      </tr>     </thead>     <tbody class="tbody">      <tr> <td>8.2.0</td> <td>  现在返回类型为 <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.true">true</a></span></strong>；之前是 <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.boolean.php" class="type bool" style="color:#EAB766">bool</a></span>。 </td></tr>     </tbody>       </table>   </div> <div class="refsect1 examples" id="refsect1-arrayobject.asort-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-3831">    <p><strong>示例 #1 <span class="function"><strong>ArrayObject::asort()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br>\$fruits </span><span style="color: #007700">= array(</span><span style="color: #DD0000">"d" </span><span style="color: #007700">=&gt; </span><span style="color: #DD0000">"lemon"</span><span style="color: #007700">, </span><span style="color: #DD0000">"a" </span><span style="color: #007700">=&gt; </span><span style="color: #DD0000">"orange"</span><span style="color: #007700">, </span><span style="color: #DD0000">"b" </span><span style="color: #007700">=&gt; </span><span style="color: #DD0000">"banana"</span><span style="color: #007700">, </span><span style="color: #DD0000">"c" </span><span style="color: #007700">=&gt; </span><span style="color: #DD0000">"apple"</span><span style="color: #007700">);<br></span><span style="color: #9876AA">\$fruitArrayObject </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(</span><span style="color: #9876AA">\$fruits</span><span style="color: #007700">);<br></span><span style="color: #9876AA">\$fruitArrayObject</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">asort</span><span style="color: #007700">();<br><br>foreach (</span><span style="color: #9876AA">\$fruitArrayObject </span><span style="color: #007700">as </span><span style="color: #9876AA">\$key </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">\$val</span><span style="color: #007700">) {<br>    echo </span><span style="color: #DD0000">"</span><span style="color: #9876AA">\$key</span><span style="color: #DD0000"> = </span><span style="color: #9876AA">\$val</span><span style="color: #DD0000">\n"</span><span style="color: #007700">;<br>}<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">c&nbsp;=&nbsp;apple<br>b&nbsp;=&nbsp;banana<br>d&nbsp;=&nbsp;lemon<br>a&nbsp;=&nbsp;orange</blockquote></div>    </div>    <div class="example-contents"><p>     The fruits have been sorted in alphabetical order, and the key     associated with each entry has been maintained.    </p></div>   </div>   </div> <div class="refsect1 seealso" id="refsect1-arrayobject.asort-seealso">  <h3 class="title">参见</h3>  <p class="para">   </p><ul class="simplelist">    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::ksort()} - Sort the entries by key</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::natsort()} - Sort entries using a "natural order" algorithm</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::natcasesort()} - Sort an array using a case insensitive "natural order" algorithm</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::uasort()} - Sort the entries with a user-defined comparison function and maintain key association</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::uksort()} - Sort the entries by keys using a user-defined comparison function</span></li>    <li><span class="function">{@link asort()} - 对数组进行升序排序并保持索引关系</span></li>   </ul>   </div></div>
 * 
     * Sort the entries by value
     * @link https://php.net/manual/zh/arrayobject.asort.php
     */
    #[TentativeType]
    #[LanguageLevelTypeAware(['8.3' => 'true'], default: 'bool')]
    public function asort(#[PhpStormStubsElementAvailable(from: '8.0')] int $flags = SORT_REGULAR) {}

    /**
 * <div id="arrayobject.ksort" class="refentry"> <div class="refnamediv">  <h1 class="refname">ArrayObject::ksort</h1>  <p class="verinfo">(PHP 5 &gt;= 5.2.0, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">ArrayObject::ksort</span> — <span class="dc-title">Sort the entries by key</span></p> </div> <div class="refsect1 description" id="refsect1-arrayobject.ksort-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>ArrayObject::ksort</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.integer.php" class="type int" style="color:#EAB766">int</a></span> <span class="parameter" style="color:#3A95FF">\$flags</span><span class="initializer"> = <strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-regular">SORT_REGULAR</a></span></strong></span></span>): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.singleton.php" class="type true" style="color:#EAB766">true</a></span></div>  <p class="para rdfs-comment">   Sorts the entries by key, maintaining key to entry correlations. This is   useful mainly for associative arrays.  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:  </p><p class="para">  如果两个成员完全相同，那么它们将保持原来的顺序。  在 PHP 8.0.0 之前，它们在排序数组中的相对顺序是未定义的。 </p></blockquote> </div> <div class="refsect1 parameters" id="refsect1-arrayobject.ksort-parameters">  <h3 class="title">参数</h3>  <dl>     <dt><span class="parameter" style="color:#3A95FF">flags</span></dt>  <dd>  <p class="para">    可选的第二个参数 <span class="parameter" style="color:#3A95FF">flags</span>     可以用以下值改变排序的行为：  </p>  <p class="para">    排序类型标记：    </p><ul class="itemizedlist">    <li class="listitem">      <span class="simpara"><strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-regular">SORT_REGULAR</a></span></strong>  - 正常比较单元         详细描述参见 <a href="https://php.net/manual/zh/language.operators.comparison.php" class="link">比较运算符</a> 章节</span>    </li>    <li class="listitem">      <span class="simpara"><strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-numeric">SORT_NUMERIC</a></span></strong> - 单元被作为数字来比较</span>    </li>    <li class="listitem">      <span class="simpara"><strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-string">SORT_STRING</a></span></strong> - 单元被作为字符串来比较</span>    </li>    <li class="listitem">      <span class="simpara">      <strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-locale-string">SORT_LOCALE_STRING</a></span></strong> -      根据当前的区域（locale）设置来把单元当作字符串比较，可以用      <span class="function">{@link setlocale()}</span> 来改变。      </span>    </li>    <li class="listitem">      <span class="simpara">      <strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-natural">SORT_NATURAL</a></span></strong> - 和 <span class="function">{@link natsort()}</span>      类似对每个单元以“自然的顺序”对字符串进行排序。      </span>    </li>    <li class="listitem">      <span class="simpara">      <strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-flag-case">SORT_FLAG_CASE</a></span></strong> - 能够与      <strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-string">SORT_STRING</a></span></strong> 或         <strong><span><a href="https://php.net/manual/zh/array.constants.php#constant.sort-natural">SORT_NATURAL</a></span></strong>          合并（OR 位运算），不区分大小写排序字符串。      </span>    </li>    </ul>    </dd>  </dl> </div> <div class="refsect1 returnvalues" id="refsect1-arrayobject.ksort-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   总是返回 <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.true">true</a></span></strong>。  </p> </div> <div class="refsect1 changelog" id="refsect1-arrayobject.ksort-changelog">  <h3 class="title">更新日志</h3>  <p class="para">   </p><table class="doctable informaltable">         <thead>      <tr>       <th>版本</th>       <th>说明</th>      </tr>     </thead>     <tbody class="tbody">      <tr> <td>8.2.0</td> <td>  现在返回类型为 <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.true">true</a></span></strong>；之前是 <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.boolean.php" class="type bool" style="color:#EAB766">bool</a></span>。 </td></tr>     </tbody>       </table>   </div> <div class="refsect1 examples" id="refsect1-arrayobject.ksort-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-3839">    <p><strong>示例 #1 <span class="function"><strong>ArrayObject::ksort()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br>\$fruits </span><span style="color: #007700">= array(</span><span style="color: #DD0000">"d" </span><span style="color: #007700">=&gt; </span><span style="color: #DD0000">"lemon"</span><span style="color: #007700">, </span><span style="color: #DD0000">"a" </span><span style="color: #007700">=&gt; </span><span style="color: #DD0000">"orange"</span><span style="color: #007700">, </span><span style="color: #DD0000">"b" </span><span style="color: #007700">=&gt; </span><span style="color: #DD0000">"banana"</span><span style="color: #007700">, </span><span style="color: #DD0000">"c" </span><span style="color: #007700">=&gt; </span><span style="color: #DD0000">"apple"</span><span style="color: #007700">);<br></span><span style="color: #9876AA">\$fruitArrayObject </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(</span><span style="color: #9876AA">\$fruits</span><span style="color: #007700">);<br></span><span style="color: #9876AA">\$fruitArrayObject</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">ksort</span><span style="color: #007700">();<br><br>foreach (</span><span style="color: #9876AA">\$fruitArrayObject </span><span style="color: #007700">as </span><span style="color: #9876AA">\$key </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">\$val</span><span style="color: #007700">) {<br>    echo </span><span style="color: #DD0000">"</span><span style="color: #9876AA">\$key</span><span style="color: #DD0000"> = </span><span style="color: #9876AA">\$val</span><span style="color: #DD0000">\n"</span><span style="color: #007700">;<br>}<br> </span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">a&nbsp;=&nbsp;orange<br>b&nbsp;=&nbsp;banana<br>c&nbsp;=&nbsp;apple<br>d&nbsp;=&nbsp;lemon</blockquote></div>    </div>   </div>   </div> <div class="refsect1 seealso" id="refsect1-arrayobject.ksort-seealso">  <h3 class="title">参见</h3>  <p class="para">   </p><ul class="simplelist">    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::asort()} - Sort the entries by value</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::natsort()} - Sort entries using a "natural order" algorithm</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::natcasesort()} - Sort an array using a case insensitive "natural order" algorithm</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::uasort()} - Sort the entries with a user-defined comparison function and maintain key association</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::uksort()} - Sort the entries by keys using a user-defined comparison function</span></li>    <li><span class="function">{@link ksort()} - 对数组根据键名升序排序</span></li>   </ul>   </div></div>
 * 
     * Sort the entries by key
     * @link https://php.net/manual/zh/arrayobject.ksort.php
     */
    #[TentativeType]
    #[LanguageLevelTypeAware(['8.3' => 'true'], default: 'bool')]
    public function ksort(#[PhpStormStubsElementAvailable(from: '8.0')] int $flags = SORT_REGULAR) {}

    /**
 * <div id="arrayobject.uasort" class="refentry"> <div class="refnamediv">  <h1 class="refname">ArrayObject::uasort</h1>  <p class="verinfo">(PHP 5 &gt;= 5.2.0, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">ArrayObject::uasort</span> — <span class="dc-title">Sort the entries with a user-defined comparison function and maintain key association</span></p> </div> <div class="refsect1 description" id="refsect1-arrayobject.uasort-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>ArrayObject::uasort</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.callable.php" class="type callable" style="color:#EAB766">callable</a></span> <span class="parameter" style="color:#3A95FF">\$callback</span></span>): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.singleton.php" class="type true" style="color:#EAB766">true</a></span></div>  <p class="para rdfs-comment">   This function sorts the entries such that keys maintain their   correlation with the entry that they are associated with, using a   user-defined comparison function.  </p>  <p class="para">   This is used mainly when sorting associative arrays where the actual   element order is significant.  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:  </p><p class="para">  如果两个成员完全相同，那么它们将保持原来的顺序。  在 PHP 8.0.0 之前，它们在排序数组中的相对顺序是未定义的。 </p></blockquote> </div> <div class="refsect1 parameters" id="refsect1-arrayobject.uasort-parameters">  <h3 class="title">参数</h3>  <p class="para">   </p><dl>         <dt><span class="parameter" style="color:#3A95FF">callback</span></dt>     <dd>      <p class="para"> 在第一个参数小于，等于或大于第二个参数时，该比较函数必须相应地返回一个小于，等于或大于 0 的整数。</p><div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><span class="replaceable">callback</span></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.mixed.php" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">\$a</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.mixed.php" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">\$b</span></span>): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.integer.php" class="type int" style="color:#EAB766">int</a></span></div><div class="caution"><strong class="caution">警告</strong> <p class="para">  从比较函数中返回<em>非整数</em>值，例如 <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.float.php" class="type float" style="color:#EAB766">float</a></span>，将导致内部强制转换为 callback 返回值为  <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.integer.php" class="type int" style="color:#EAB766">int</a></span>。因此，诸如 <span class="literal">0.99</span> 和 <span class="literal">0.1</span> 之类的值都将被转换为整数值  <span class="literal">0</span>，将这些值比较的话将会是相等。 </p></div>     </dd>       </dl>   </div> <div class="refsect1 returnvalues" id="refsect1-arrayobject.uasort-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   总是返回 <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.true">true</a></span></strong>。  </p> </div> <div class="refsect1 changelog" id="refsect1-arrayobject.uasort-changelog">  <h3 class="title">更新日志</h3>  <p class="para">   </p><table class="doctable informaltable">         <thead>      <tr>       <th>版本</th>       <th>说明</th>      </tr>     </thead>     <tbody class="tbody">      <tr> <td>8.2.0</td> <td>  现在返回类型为 <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.true">true</a></span></strong>；之前是 <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.boolean.php" class="type bool" style="color:#EAB766">bool</a></span>。 </td></tr>     </tbody>       </table>   </div> <div class="refsect1 examples" id="refsect1-arrayobject.uasort-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-3849">    <p><strong>示例 #1 <span class="function"><strong>ArrayObject::uasort()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br></span><span style="color: #FF8000">// Comparison function<br></span><span style="color: #007700">function </span><span style="color: #9876AA">cmp</span><span style="color: #007700">(</span><span style="color: #9876AA">\$a</span><span style="color: #007700">, </span><span style="color: #9876AA">\$b</span><span style="color: #007700">) {<br>    if (</span><span style="color: #9876AA">\$a </span><span style="color: #007700">== </span><span style="color: #9876AA">\$b</span><span style="color: #007700">) {<br>        return </span><span style="color: #9876AA">0</span><span style="color: #007700">;<br>    }<br>    return (</span><span style="color: #9876AA">\$a </span><span style="color: #007700">&lt; </span><span style="color: #9876AA">\$b</span><span style="color: #007700">) ? -</span><span style="color: #9876AA">1 </span><span style="color: #007700">: </span><span style="color: #9876AA">1</span><span style="color: #007700">;<br>}<br><br></span><span style="color: #FF8000">// Array to be sorted<br></span><span style="color: #9876AA">\$array </span><span style="color: #007700">= array(</span><span style="color: #DD0000">'a' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">4</span><span style="color: #007700">, </span><span style="color: #DD0000">'b' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">8</span><span style="color: #007700">, </span><span style="color: #DD0000">'c' </span><span style="color: #007700">=&gt; -</span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #DD0000">'d' </span><span style="color: #007700">=&gt; -</span><span style="color: #9876AA">9</span><span style="color: #007700">, </span><span style="color: #DD0000">'e' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">2</span><span style="color: #007700">, </span><span style="color: #DD0000">'f' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">5</span><span style="color: #007700">, </span><span style="color: #DD0000">'g' </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">3</span><span style="color: #007700">, </span><span style="color: #DD0000">'h' </span><span style="color: #007700">=&gt; -</span><span style="color: #9876AA">4</span><span style="color: #007700">);<br></span><span style="color: #9876AA">\$arrayObject </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(</span><span style="color: #9876AA">\$array</span><span style="color: #007700">);<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$arrayObject</span><span style="color: #007700">);<br><br></span><span style="color: #FF8000">// Sort and print the resulting array<br></span><span style="color: #9876AA">\$arrayObject</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">uasort</span><span style="color: #007700">(</span><span style="color: #DD0000">'cmp'</span><span style="color: #007700">);<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$arrayObject</span><span style="color: #007700">);<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">object(ArrayObject)#1&nbsp;(1)&nbsp;{<br>&nbsp;&nbsp;["storage":"ArrayObject":private]=&gt;<br>&nbsp;&nbsp;array(8)&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;["a"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(4)<br>&nbsp;&nbsp;&nbsp;&nbsp;["b"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(8)<br>&nbsp;&nbsp;&nbsp;&nbsp;["c"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(-1)<br>&nbsp;&nbsp;&nbsp;&nbsp;["d"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(-9)<br>&nbsp;&nbsp;&nbsp;&nbsp;["e"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(2)<br>&nbsp;&nbsp;&nbsp;&nbsp;["f"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(5)<br>&nbsp;&nbsp;&nbsp;&nbsp;["g"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(3)<br>&nbsp;&nbsp;&nbsp;&nbsp;["h"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(-4)<br>&nbsp;&nbsp;}<br>}<br>object(ArrayObject)#1&nbsp;(1)&nbsp;{<br>&nbsp;&nbsp;["storage":"ArrayObject":private]=&gt;<br>&nbsp;&nbsp;array(8)&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;["d"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(-9)<br>&nbsp;&nbsp;&nbsp;&nbsp;["h"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(-4)<br>&nbsp;&nbsp;&nbsp;&nbsp;["c"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(-1)<br>&nbsp;&nbsp;&nbsp;&nbsp;["e"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(2)<br>&nbsp;&nbsp;&nbsp;&nbsp;["g"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(3)<br>&nbsp;&nbsp;&nbsp;&nbsp;["a"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(4)<br>&nbsp;&nbsp;&nbsp;&nbsp;["f"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(5)<br>&nbsp;&nbsp;&nbsp;&nbsp;["b"]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;int(8)<br>&nbsp;&nbsp;}<br>}</blockquote></div>    </div>   </div>   </div> <div class="refsect1 seealso" id="refsect1-arrayobject.uasort-seealso">  <h3 class="title">参见</h3>  <p class="para">   </p><ul class="simplelist">    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::asort()} - Sort the entries by value</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::ksort()} - Sort the entries by key</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::natsort()} - Sort entries using a "natural order" algorithm</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::natcasesort()} - Sort an array using a case insensitive "natural order" algorithm</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::uksort()} - Sort the entries by keys using a user-defined comparison function</span></li>    <li><span class="function">{@link uasort()} - 使用用户定义的比较函数对数组进行排序并保持索引关联</span></li>   </ul>   </div></div>
 * 
     * Sort the entries with a user-defined comparison function and maintain key association
     * @link https://php.net/manual/zh/arrayobject.uasort.php
     * @param callable(TValue, TValue):int $callback <p>
     * Function <i>cmp_function</i> should accept two
     * parameters which will be filled by pairs of entries.
     * The comparison function must return an integer less than, equal
     * to, or greater than zero if the first argument is considered to
     * be respectively less than, equal to, or greater than the
     * second.
     * </p>
     */
    #[TentativeType]
    #[LanguageLevelTypeAware(['8.3' => 'true'], default: 'bool')]
    public function uasort(#[LanguageLevelTypeAware(['8.0' => 'callable'], default: '')] $callback) {}

    /**
 * <div id="arrayobject.uksort" class="refentry"> <div class="refnamediv">  <h1 class="refname">ArrayObject::uksort</h1>  <p class="verinfo">(PHP 5 &gt;= 5.2.0, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">ArrayObject::uksort</span> — <span class="dc-title">Sort the entries by keys using a user-defined comparison function</span></p> </div> <div class="refsect1 description" id="refsect1-arrayobject.uksort-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>ArrayObject::uksort</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.callable.php" class="type callable" style="color:#EAB766">callable</a></span> <span class="parameter" style="color:#3A95FF">\$callback</span></span>): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.singleton.php" class="type true" style="color:#EAB766">true</a></span></div>  <p class="para rdfs-comment">   This function sorts the keys of the entries using a user-supplied   comparison function. The key to entry correlations will be maintained.  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:  </p><p class="para">  如果两个成员完全相同，那么它们将保持原来的顺序。  在 PHP 8.0.0 之前，它们在排序数组中的相对顺序是未定义的。 </p></blockquote> </div> <div class="refsect1 parameters" id="refsect1-arrayobject.uksort-parameters">  <h3 class="title">参数</h3>  <p class="para">   </p><dl>         <dt><span class="parameter" style="color:#3A95FF">callback</span></dt>     <dd>      <p class="para"> 在第一个参数小于，等于或大于第二个参数时，该比较函数必须相应地返回一个小于，等于或大于 0 的整数。</p><div class="methodsynopsis dc-description"><span class="methodname" style="color:#CC7832"><span class="replaceable">callback</span></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.mixed.php" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">\$a</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.mixed.php" class="type mixed" style="color:#EAB766">mixed</a></span> <span class="parameter" style="color:#3A95FF">\$b</span></span>): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.integer.php" class="type int" style="color:#EAB766">int</a></span></div><div class="caution"><strong class="caution">警告</strong> <p class="para">  从比较函数中返回<em>非整数</em>值，例如 <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.float.php" class="type float" style="color:#EAB766">float</a></span>，将导致内部强制转换为 callback 返回值为  <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.integer.php" class="type int" style="color:#EAB766">int</a></span>。因此，诸如 <span class="literal">0.99</span> 和 <span class="literal">0.1</span> 之类的值都将被转换为整数值  <span class="literal">0</span>，将这些值比较的话将会是相等。 </p></div>     </dd>       </dl>   </div> <div class="refsect1 returnvalues" id="refsect1-arrayobject.uksort-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   总是返回 <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.true">true</a></span></strong>。  </p> </div> <div class="refsect1 changelog" id="refsect1-arrayobject.uksort-changelog">  <h3 class="title">更新日志</h3>  <p class="para">   </p><table class="doctable informaltable">         <thead>      <tr>       <th>版本</th>       <th>说明</th>      </tr>     </thead>     <tbody class="tbody">      <tr> <td>8.2.0</td> <td>  现在返回类型为 <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.true">true</a></span></strong>；之前是 <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.boolean.php" class="type bool" style="color:#EAB766">bool</a></span>。 </td></tr>     </tbody>       </table>   </div> <div class="refsect1 examples" id="refsect1-arrayobject.uksort-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-3850">    <p><strong>示例 #1 <span class="function"><strong>ArrayObject::uksort()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br></span><span style="color: #007700">function </span><span style="color: #9876AA">cmp</span><span style="color: #007700">(</span><span style="color: #9876AA">\$a</span><span style="color: #007700">, </span><span style="color: #9876AA">\$b</span><span style="color: #007700">) {<br>    </span><span style="color: #9876AA">\$a </span><span style="color: #007700">= </span><span style="color: #9876AA">preg_replace</span><span style="color: #007700">(</span><span style="color: #DD0000">'@^(a|an|the) @'</span><span style="color: #007700">, </span><span style="color: #DD0000">''</span><span style="color: #007700">, </span><span style="color: #9876AA">\$a</span><span style="color: #007700">);<br>    </span><span style="color: #9876AA">\$b </span><span style="color: #007700">= </span><span style="color: #9876AA">preg_replace</span><span style="color: #007700">(</span><span style="color: #DD0000">'@^(a|an|the) @'</span><span style="color: #007700">, </span><span style="color: #DD0000">''</span><span style="color: #007700">, </span><span style="color: #9876AA">\$b</span><span style="color: #007700">);<br>    return </span><span style="color: #9876AA">strcasecmp</span><span style="color: #007700">(</span><span style="color: #9876AA">\$a</span><span style="color: #007700">, </span><span style="color: #9876AA">\$b</span><span style="color: #007700">);<br>}<br><br></span><span style="color: #9876AA">\$array </span><span style="color: #007700">= array(</span><span style="color: #DD0000">"John" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">1</span><span style="color: #007700">, </span><span style="color: #DD0000">"the Earth" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">2</span><span style="color: #007700">, </span><span style="color: #DD0000">"an apple" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">3</span><span style="color: #007700">, </span><span style="color: #DD0000">"a banana" </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">4</span><span style="color: #007700">);<br></span><span style="color: #9876AA">\$arrayObject </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(</span><span style="color: #9876AA">\$array</span><span style="color: #007700">);<br></span><span style="color: #9876AA">\$arrayObject</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">uksort</span><span style="color: #007700">(</span><span style="color: #DD0000">'cmp'</span><span style="color: #007700">);<br><br>foreach (</span><span style="color: #9876AA">\$arrayObject </span><span style="color: #007700">as </span><span style="color: #9876AA">\$key </span><span style="color: #007700">=&gt; </span><span style="color: #9876AA">\$value</span><span style="color: #007700">) {<br>    echo </span><span style="color: #DD0000">"</span><span style="color: #9876AA">\$key</span><span style="color: #DD0000">: </span><span style="color: #9876AA">\$value</span><span style="color: #DD0000">\n"</span><span style="color: #007700">;<br>}<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">an&nbsp;apple:&nbsp;3<br>a&nbsp;banana:&nbsp;4<br>the&nbsp;Earth:&nbsp;2<br>John:&nbsp;1</blockquote></div>    </div>   </div>   </div> <div class="refsect1 seealso" id="refsect1-arrayobject.uksort-seealso">  <h3 class="title">参见</h3>  <p class="para">   </p><ul class="simplelist">    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::asort()} - Sort the entries by value</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::ksort()} - Sort the entries by key</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::natsort()} - Sort entries using a "natural order" algorithm</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::natcasesort()} - Sort an array using a case insensitive "natural order" algorithm</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::uasort()} - Sort the entries with a user-defined comparison function and maintain key association</span></li>    <li><span class="function">{@link uksort()} - 使用用户自定义的比较函数对数组中的键名进行排序</span></li>   </ul>   </div></div>
 * 
     * Sort the entries by keys using a user-defined comparison function
     * @link https://php.net/manual/zh/arrayobject.uksort.php
     * @param callable(TValue, TValue):int $callback <p>
     * The callback comparison function.
     * </p>
     * <p>
     * Function <i>cmp_function</i> should accept two
     * parameters which will be filled by pairs of entry keys.
     * The comparison function must return an integer less than, equal
     * to, or greater than zero if the first argument is considered to
     * be respectively less than, equal to, or greater than the
     * second.
     * </p>
     */
    #[TentativeType]
    #[LanguageLevelTypeAware(['8.3' => 'true'], default: 'bool')]
    public function uksort(#[LanguageLevelTypeAware(['8.0' => 'callable'], default: '')] $callback) {}

    /**
 * <div id="arrayobject.natsort" class="refentry"> <div class="refnamediv">  <h1 class="refname">ArrayObject::natsort</h1>  <p class="verinfo">(PHP 5 &gt;= 5.2.0, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">ArrayObject::natsort</span> — <span class="dc-title">Sort entries using a "natural order" algorithm</span></p> </div> <div class="refsect1 description" id="refsect1-arrayobject.natsort-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>ArrayObject::natsort</strong></span>(): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.singleton.php" class="type true" style="color:#EAB766">true</a></span></div>  <p class="para rdfs-comment">   This method implements a sort algorithm that orders alphanumeric strings  in the way a human being would while maintaining key/value associations.   This is described as a "natural ordering".  An example of the difference   between this algorithm and the regular computer string sorting algorithms   (used in {@link ArrayObject::asort}) method   can be seen in the example below.  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:  </p><p class="para">  如果两个成员完全相同，那么它们将保持原来的顺序。  在 PHP 8.0.0 之前，它们在排序数组中的相对顺序是未定义的。 </p></blockquote> </div> <div class="refsect1 parameters" id="refsect1-arrayobject.natsort-parameters">  <h3 class="title">参数</h3>  <p class="para">此函数没有参数。</p> </div> <div class="refsect1 returnvalues" id="refsect1-arrayobject.natsort-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   没有返回值。  </p> </div> <div class="refsect1 changelog" id="refsect1-arrayobject.natsort-changelog">  <h3 class="title">更新日志</h3>  <p class="para">   </p><table class="doctable informaltable">         <thead>      <tr>       <th>版本</th>       <th>说明</th>      </tr>     </thead>     <tbody class="tbody">      <tr> <td>8.2.0</td> <td>  现在返回类型为 <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.true">true</a></span></strong>；之前是 <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.boolean.php" class="type bool" style="color:#EAB766">bool</a></span>。 </td></tr>     </tbody>       </table>   </div> <div class="refsect1 examples" id="refsect1-arrayobject.natsort-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-3841">    <p><strong>示例 #1 <span class="function"><strong>ArrayObject::natsort()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br>\$array </span><span style="color: #007700">= array(</span><span style="color: #DD0000">"img12.png"</span><span style="color: #007700">, </span><span style="color: #DD0000">"img10.png"</span><span style="color: #007700">, </span><span style="color: #DD0000">"img2.png"</span><span style="color: #007700">, </span><span style="color: #DD0000">"img1.png"</span><span style="color: #007700">);<br><br></span><span style="color: #9876AA">\$arr1 </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(</span><span style="color: #9876AA">\$array</span><span style="color: #007700">);<br></span><span style="color: #9876AA">\$arr2 </span><span style="color: #007700">= clone </span><span style="color: #9876AA">\$arr1</span><span style="color: #007700">;<br><br></span><span style="color: #9876AA">\$arr1</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">asort</span><span style="color: #007700">();<br>echo </span><span style="color: #DD0000">"Standard sorting\n"</span><span style="color: #007700">;<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$arr1</span><span style="color: #007700">);<br><br></span><span style="color: #9876AA">\$arr2</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">natsort</span><span style="color: #007700">();<br>echo </span><span style="color: #DD0000">"\nNatural order sorting\n"</span><span style="color: #007700">;<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$arr2</span><span style="color: #007700">);<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">Standard&nbsp;sorting<br>object(ArrayObject)#1&nbsp;(1)&nbsp;{<br>&nbsp;&nbsp;["storage":"ArrayObject":private]=&gt;<br>&nbsp;&nbsp;array(4)&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;[3]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(8)&nbsp;"img1.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[1]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(9)&nbsp;"img10.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[0]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(9)&nbsp;"img12.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[2]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(8)&nbsp;"img2.png"<br>&nbsp;&nbsp;}<br>}<br><br>Natural&nbsp;order&nbsp;sorting<br>object(ArrayObject)#2&nbsp;(1)&nbsp;{<br>&nbsp;&nbsp;["storage":"ArrayObject":private]=&gt;<br>&nbsp;&nbsp;array(4)&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;[3]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(8)&nbsp;"img1.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[2]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(8)&nbsp;"img2.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[1]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(9)&nbsp;"img10.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[0]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(9)&nbsp;"img12.png"<br>&nbsp;&nbsp;}<br>}</blockquote></div>    </div>    <div class="example-contents"><p>     For more information see: Martin Pool's <a href="https://github.com/sourcefrog/natsort" class="link external">»&nbsp;Natural Order String Comparison</a>     page.    </p></div>   </div>   </div> <div class="refsect1 seealso" id="refsect1-arrayobject.natsort-seealso">  <h3 class="title">参见</h3>  <p class="para">   </p><ul class="simplelist">    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::asort()} - Sort the entries by value</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::ksort()} - Sort the entries by key</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::natcasesort()} - Sort an array using a case insensitive "natural order" algorithm</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::uasort()} - Sort the entries with a user-defined comparison function and maintain key association</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::uksort()} - Sort the entries by keys using a user-defined comparison function</span></li>    <li><span class="function">{@link natsort()} - 用“自然排序”算法对数组排序</span></li>   </ul>   </div></div>
 * 
     * Sort entries using a "natural order" algorithm
     * @link https://php.net/manual/zh/arrayobject.natsort.php
     */
    #[TentativeType]
    #[LanguageLevelTypeAware(['8.3' => 'true'], default: 'bool')]
    public function natsort() {}

    /**
 * <div id="arrayobject.natcasesort" class="refentry"> <div class="refnamediv">  <h1 class="refname">ArrayObject::natcasesort</h1>  <p class="verinfo">(PHP 5 &gt;= 5.2.0, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">ArrayObject::natcasesort</span> — <span class="dc-title">Sort an array using a case insensitive "natural order" algorithm</span></p> </div> <div class="refsect1 description" id="refsect1-arrayobject.natcasesort-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="modifier">public</span> <span class="methodname" style="color:#CC7832"><strong>ArrayObject::natcasesort</strong></span>(): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.singleton.php" class="type true" style="color:#EAB766">true</a></span></div>  <p class="para rdfs-comment">   This method is a case insensitive version of   {@link ArrayObject::natsort}.  </p>  <p class="para">   This method implements a sort algorithm that orders   alphanumeric strings in the way a human being would while maintaining   key/value associations.  This is described as a "natural ordering".  </p>  <blockquote class="note" style="border:1px gray solid"><p><strong class="note" style="border:1px gray solid">注意</strong>:  </p><p class="para">  如果两个成员完全相同，那么它们将保持原来的顺序。  在 PHP 8.0.0 之前，它们在排序数组中的相对顺序是未定义的。 </p></blockquote> </div> <div class="refsect1 parameters" id="refsect1-arrayobject.natcasesort-parameters">  <h3 class="title">参数</h3>  <p class="para">此函数没有参数。</p> </div> <div class="refsect1 returnvalues" id="refsect1-arrayobject.natcasesort-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   没有返回值。  </p> </div> <div class="refsect1 changelog" id="refsect1-arrayobject.natcasesort-changelog">  <h3 class="title">更新日志</h3>  <p class="para">   </p><table class="doctable informaltable">         <thead>      <tr>       <th>版本</th>       <th>说明</th>      </tr>     </thead>     <tbody class="tbody">      <tr> <td>8.2.0</td> <td>  现在返回类型为 <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.true">true</a></span></strong>；之前是 <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.boolean.php" class="type bool" style="color:#EAB766">bool</a></span>。 </td></tr>     </tbody>       </table>   </div> <div class="refsect1 examples" id="refsect1-arrayobject.natcasesort-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-3840">    <p><strong>示例 #1 <span class="function"><strong>ArrayObject::natcasesort()</strong></span> example</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br>\$array </span><span style="color: #007700">= array(</span><span style="color: #DD0000">'IMG0.png'</span><span style="color: #007700">, </span><span style="color: #DD0000">'img12.png'</span><span style="color: #007700">, </span><span style="color: #DD0000">'img10.png'</span><span style="color: #007700">, </span><span style="color: #DD0000">'img2.png'</span><span style="color: #007700">, </span><span style="color: #DD0000">'img1.png'</span><span style="color: #007700">, </span><span style="color: #DD0000">'IMG3.png'</span><span style="color: #007700">);<br><br></span><span style="color: #9876AA">\$arr1 </span><span style="color: #007700">= new </span><span style="color: #9876AA">ArrayObject</span><span style="color: #007700">(</span><span style="color: #9876AA">\$array</span><span style="color: #007700">);<br></span><span style="color: #9876AA">\$arr2 </span><span style="color: #007700">= clone </span><span style="color: #9876AA">\$arr1</span><span style="color: #007700">;<br><br></span><span style="color: #9876AA">\$arr1</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">asort</span><span style="color: #007700">();<br>echo </span><span style="color: #DD0000">"Standard sorting\n"</span><span style="color: #007700">;<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$arr1</span><span style="color: #007700">);<br><br></span><span style="color: #9876AA">\$arr2</span><span style="color: #007700">-&gt;</span><span style="color: #9876AA">natcasesort</span><span style="color: #007700">();<br>echo </span><span style="color: #DD0000">"\nNatural order sorting (case-insensitive)\n"</span><span style="color: #007700">;<br></span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">\$arr2</span><span style="color: #007700">);<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">Standard&nbsp;sorting<br>object(ArrayObject)#1&nbsp;(1)&nbsp;{<br>&nbsp;&nbsp;["storage":"ArrayObject":private]=&gt;<br>&nbsp;&nbsp;array(6)&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;[0]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(8)&nbsp;"IMG0.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[5]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(8)&nbsp;"IMG3.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[4]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(8)&nbsp;"img1.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[2]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(9)&nbsp;"img10.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[1]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(9)&nbsp;"img12.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[3]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(8)&nbsp;"img2.png"<br>&nbsp;&nbsp;}<br>}<br><br>Natural&nbsp;order&nbsp;sorting&nbsp;(case-insensitive)<br>object(ArrayObject)#2&nbsp;(1)&nbsp;{<br>&nbsp;&nbsp;["storage":"ArrayObject":private]=&gt;<br>&nbsp;&nbsp;array(6)&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;[0]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(8)&nbsp;"IMG0.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[4]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(8)&nbsp;"img1.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[3]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(8)&nbsp;"img2.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[5]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(8)&nbsp;"IMG3.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[2]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(9)&nbsp;"img10.png"<br>&nbsp;&nbsp;&nbsp;&nbsp;[1]=&gt;<br>&nbsp;&nbsp;&nbsp;&nbsp;string(9)&nbsp;"img12.png"<br>&nbsp;&nbsp;}<br>}</blockquote></div>    </div>    <div class="example-contents"><p>     For more information see: Martin Pool's <a href="https://github.com/sourcefrog/natsort" class="link external">»&nbsp;Natural Order String Comparison</a>     page.    </p></div>   </div>   </div> <div class="refsect1 seealso" id="refsect1-arrayobject.natcasesort-seealso">  <h3 class="title">参见</h3>  <p class="para">   </p><ul class="simplelist">    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::asort()} - Sort the entries by value</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::ksort()} - Sort the entries by key</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::natsort()} - Sort entries using a "natural order" algorithm</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::uasort()} - Sort the entries with a user-defined comparison function and maintain key association</span></li>    <li><span class="methodname" style="color:#CC7832">{@link ArrayObject::uksort()} - Sort the entries by keys using a user-defined comparison function</span></li>    <li><span class="function">{@link natcasesort()} - 用“自然排序”算法对数组进行不区分大小写字母的排序</span></li>   </ul>   </div></div>
 * 
     * Sort an array using a case insensitive "natural order" algorithm
     * @link https://php.net/manual/zh/arrayobject.natcasesort.php
     */
    #[TentativeType]
    #[LanguageLevelTypeAware(['8.3' => 'true'], default: 'bool')]
    public function natcasesort() {}

