function jddayofweek(int $julian_day, int $mode = CAL_DOW_DAYNO): string|int {}

/**
 * <div id="function.jdmonthname" class="refentry"> <div class="refnamediv">  <h1 class="refname">jdmonthname</h1>  <p class="verinfo">(PHP 4, PHP 5, PHP 7, PHP 8)</p><p class="refpurpose"><span class="refname">jdmonthname</span> — <span class="dc-title">返回月份名称</span></p> </div> <div class="refsect1 description" id="refsect1-function.jdmonthname-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>jdmonthname</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.integer.php" class="type int" style="color:#EAB766">int</a></span> <span class="parameter" style="color:#3A95FF">\$julian_day</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.integer.php" class="type int" style="color:#EAB766">int</a></span> <span class="parameter" style="color:#3A95FF">\$mode</span></span>): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.string.php" class="type string" style="color:#EAB766">string</a></span></div>  <p class="para rdfs-comment">   返回 string 类型的月份名称，<span class="parameter" style="color:#3A95FF">mode</span>    告诉此函数要将儒略历转化为哪个历法以及哪个类型的月份名称。   </p><table class="doctable table">    <caption><strong>历法模式</strong></caption>         <thead>      <tr>       <th>模式</th>       <th>含义</th>       <th>值</th>      </tr>     </thead>     <tbody class="tbody">      <tr>       <td><strong><span><a href="https://php.net/manual/zh/calendar.constants.php#constant.cal-month-gregorian-short">CAL_MONTH_GREGORIAN_SHORT</a></span></strong></td>       <td>公历 - 缩写</td>       <td>Jan, Feb, Mar, Apr, May, Jun, Jul, Aug, Sep, Oct, Nov, Dec</td>      </tr>      <tr>       <td><strong><span><a href="https://php.net/manual/zh/calendar.constants.php#constant.cal-month-gregorian-long">CAL_MONTH_GREGORIAN_LONG</a></span></strong></td>       <td>公历</td>       <td>January, February, March, April, May, June, July, August, September, October, November, December</td>      </tr>      <tr>       <td><strong><span><a href="https://php.net/manual/zh/calendar.constants.php#constant.cal-month-julian-short">CAL_MONTH_JULIAN_SHORT</a></span></strong></td>       <td>儒略历 - 缩写</td>       <td>Jan, Feb, Mar, Apr, May, Jun, Jul, Aug, Sep, Oct, Nov, Dec</td>      </tr>      <tr>       <td><strong><span><a href="https://php.net/manual/zh/calendar.constants.php#constant.cal-month-julian-long">CAL_MONTH_JULIAN_LONG</a></span></strong></td>       <td>儒略历</td>       <td>January, February, March, April, May, June, July, August, September, October, November, December</td>      </tr>      <tr>       <td><strong><span><a href="https://php.net/manual/zh/calendar.constants.php#constant.cal-month-jewish">CAL_MONTH_JEWISH</a></span></strong></td>       <td>犹太历</td>       <td>Tishri, Heshvan, Kislev, Tevet, Shevat, Adar, Adar I, Adar II, Nisan, Iyyar, Sivan, Tammuz, Av, Elul</td>      </tr>      <tr>       <td><strong><span><a href="https://php.net/manual/zh/calendar.constants.php#constant.cal-month-french">CAL_MONTH_FRENCH</a></span></strong></td>       <td>法国历</td>       <td>Vendemiaire, Brumaire, Frimaire, Nivose, Pluviose, Ventose, Germinal, Floreal, Prairial, Messidor, Thermidor, Fructidor, Extra</td>      </tr>     </tbody>       </table>   </div> <div class="refsect1 parameters" id="refsect1-function.jdmonthname-parameters">  <h3 class="title">参数</h3>  <p class="para">   </p><dl>         <dt><span class="parameter" style="color:#3A95FF">jday</span></dt>     <dd>      <p class="para">       用来计算的儒略日      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">mode</span></dt>     <dd>      <p class="para">       历法模式（见上表）。      </p>     </dd>       </dl>   </div> <div class="refsect1 returnvalues" id="refsect1-function.jdmonthname-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   指定的儒略日和 <span class="parameter" style="color:#3A95FF">mode</span> 的月份的名称。  </p> </div></div>
 * 
 * Returns a month name
 * @link https://php.net/manual/zh/function.jdmonthname.php
