an style="color: #007700">(</span><span style="color: #9876AA">$data</span><span style="color: #007700">);<br>    </span><span style="color: #9876AA">var_dump</span><span style="color: #007700">(</span><span style="color: #9876AA">$result</span><span style="color: #007700">);<br>}<br><br>function </span><span style="color: #9876AA">my_open_cb</span><span style="color: #007700">(</span><span style="color: #9876AA">$data</span><span style="color: #007700">, </span><span style="color: #9876AA">$result</span><span style="color: #007700">) {<br>    </span><span style="color: #9876AA">eio_close</span><span style="color: #007700">(</span><span style="color: #9876AA">$result</span><span style="color: #007700">);<br>    </span><span style="color: #9876AA">eio_event_loop</span><span style="color: #007700">();<br><br>    @</span><span style="color: #9876AA">unlink</span><span style="color: #007700">(</span><span style="color: #9876AA">$data</span><span style="color: #007700">);<br>}<br><br></span><span style="color: #9876AA">eio_stat</span><span style="color: #007700">(</span><span style="color: #9876AA">$tmp_filename</span><span style="color: #007700">, </span><span style="color: #9876AA">EIO_PRI_DEFAULT</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_res_cb"</span><span style="color: #007700">, </span><span style="color: #DD0000">"eio_stat"</span><span style="color: #007700">);<br></span><span style="color: #9876AA">eio_open</span><span style="color: #007700">(</span><span style="color: #9876AA">$tmp_filename</span><span style="color: #007700">, </span><span style="color: #9876AA">EIO_O_RDONLY</span><span style="color: #007700">, </span><span style="color: #9876AA">NULL</span><span style="color: #007700">,<br> </span><span style="color: #9876AA">EIO_PRI_DEFAULT</span><span style="color: #007700">, </span><span style="color: #DD0000">"my_open_cb"</span><span style="color: #007700">, </span><span style="color: #9876AA">$tmp_filename</span><span sty