</span><span style="color: #007700">) or die(</span><span style="color: #DD0000">"Could not connect"</span><span style="color: #007700">);<br><br>  </span><span style="color: #9876AA">$res </span><span style="color: #007700">= </span><span style="color: #9876AA">pg_query</span><span style="color: #007700">(</span><span style="color: #9876AA">$dbconn</span><span style="color: #007700">, </span><span style="color: #DD0000">"select author, year, title from authors where author = 'Orwell'"</span><span style="color: #007700">);<br>  <br>  echo </span><span style="color: #DD0000">"Column 'title' is field number: "</span><span style="color: #007700">, </span><span style="color: #9876AA">pg_field_num</span><span style="color: #007700">(</span><span style="color: #9876AA">$res</span><span style="color: #007700">, </span><span style="color: #DD0000">'title'</span><span style="color: #007700">);<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>    <div class="example-contents"><p>以上示例会输出：</p></div>    <div class="example-contents screen" style="border-color:gray;background:#1E1F22;"><div class="examplescode"><blockquote style="border:1px gray solid;">Column 'title' is field number: 2</blockquote></div>    </div>   </div>    </div>   <div class="refsect1 seealso" id="refsect1-function.pg-field-num-seealso">  <h3 class="title">参见</h3>  <p class="para">   </p><ul class="simplelist">    <li><span class="function">{@link pg_field_name()} - 返回字段名</span></li>   </ul>   </div></div>
