/**
 * <div id="function.ssh2-auth-none" class="refentry"> <div class="refnamediv">  <h1 class="refname">ssh2_auth_none</h1>  <p class="verinfo">(PECL ssh2 &gt;= 0.9.0)</p><p class="refpurpose"><span class="refname">ssh2_auth_none</span> — <span class="dc-title">Authenticate as "none"</span></p> </div>  <div class="refsect1 description" id="refsect1-function.ssh2-auth-none-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>ssh2_auth_none</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.resource.php" class="type resource" style="color:#EAB766">resource</a></span> <span class="parameter" style="color:#3A95FF">\$session</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.string.php" class="type string" style="color:#EAB766">string</a></span> <span class="parameter" style="color:#3A95FF">\$username</span></span>): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.mixed.php" class="type mixed" style="color:#EAB766">mixed</a></span></div>  <p class="para rdfs-comment">   Attempt "none" authentication which usually will (and should) fail.   As part of the failure, this function will return an array of accepted   authentication methods.   </p> </div> <div class="refsect1 parameters" id="refsect1-function.ssh2-auth-none-parameters">  <h3 class="title">参数</h3>  <p class="para">   </p><dl>         <dt><span class="parameter" style="color:#3A95FF">session</span></dt>     <dd>      <p class="para">       An SSH connection link identifier, obtained from a call to       <span class="function">{@link ssh2_connect()}</span>.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">username</span></dt>     <dd>      <p class="para">       Remote user name.      </p>     </dd>       </dl>   </div> <div class="refsect1 returnvalues" id="refsect1-function.ssh2-auth-none-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   Returns <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.true">true</a></span></strong> if the server does accept "none" as an authentication   method, or an array of accepted authentication methods on failure.  </p> </div> <div class="refsect1 examples" id="refsect1-function.ssh2-auth-none-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-4788">    <p><strong>示例 #1 Retrieving a list of authentication methods</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br>\$connection </span><span style="color: #007700">= </span><span style="color: #9876AA">ssh2_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'shell.example.com'</span><span style="color: #007700">, </span><span style="color: #9876AA">22</span><span style="color: #007700">);<br><br></span><span style="color: #9876AA">\$auth_methods </span><span style="color: #007700">= </span><span style="color: #9876AA">ssh2_auth_none</span><span style="color: #007700">(</span><span style="color: #9876AA">\$connection</span><span style="color: #007700">, </span><span style="color: #DD0000">'user'</span><span style="color: #007700">);<br><br>if (</span><span style="color: #9876AA">in_array</span><span style="color: #007700">(</span><span style="color: #DD0000">'password'</span><span style="color: #007700">, </span><span style="color: #9876AA">\$auth_methods</span><span style="color: #007700">)) {<br>  echo </span><span style="color: #DD0000">"Server supports password based authentication\n"</span><span style="color: #007700">;<br>}<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>   </div>   </div></div>
 * 
 * (PECL ssh2 &gt;= 0.9.0)<br/>
 * Authenticate as "none"
 * @link https://php.net/manual/zh/function.ssh2-auth-none.php
 * @param resource $session <p>
 * An SSH connection link identifier, obtained from a call to
 * ssh2_connect.
 * </p>
 * @param string $username <p>
 * Remote user name.
 * </p>
 * @return array|bool true if the server does accept "none" as an authentication
 * method, or an array of accepted authentication methods on failure.
 */
function ssh2_auth_none($session, $username) {}

/**
 * <div id="function.ssh2-auth-password" class="refentry"> <div class="refnamediv">  <h1 class="refname">ssh2_auth_password</h1>  <p class="verinfo">(PECL ssh2 &gt;= 0.9.0)</p><p class="refpurpose"><span class="refname">ssh2_auth_password</span> — <span class="dc-title">Authenticate over SSH using a plain password</span></p> </div>  <div class="refsect1 description" id="refsect1-function.ssh2-auth-password-description">  <h3 class="title">说明</h3>  <div class="methodsynopsis dc-description">   <span class="methodname" style="color:#CC7832"><strong>ssh2_auth_password</strong></span>(<span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.resource.php" class="type resource" style="color:#EAB766">resource</a></span> <span class="parameter" style="color:#3A95FF">\$session</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.string.php" class="type string" style="color:#EAB766">string</a></span> <span class="parameter" style="color:#3A95FF">\$username</span></span>, <span class="methodparam"><span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.string.php" class="type string" style="color:#EAB766">string</a></span> <span class="parameter" style="color:#3A95FF">\$password</span></span>): <span class="type" style="color:#EAB766"><a href="https://php.net/manual/zh/language.types.boolean.php" class="type bool" style="color:#EAB766">bool</a></span></div>  <p class="para rdfs-comment">   Authenticate over SSH using a plain password. Since version 0.12 this function   also supports keyboard_interactive method.  </p> </div> <div class="refsect1 parameters" id="refsect1-function.ssh2-auth-password-parameters">  <h3 class="title">参数</h3>  <p class="para">   </p><dl>         <dt><span class="parameter" style="color:#3A95FF">session</span></dt>     <dd>      <p class="para">       An SSH connection link identifier, obtained from a call to       <span class="function">{@link ssh2_connect()}</span>.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">username</span></dt>     <dd>      <p class="para">       Remote user name.      </p>     </dd>             <dt><span class="parameter" style="color:#3A95FF">password</span></dt>     <dd>      <p class="para">       Password for <span class="parameter" style="color:#3A95FF">username</span>      </p>     </dd>       </dl>   </div> <div class="refsect1 returnvalues" id="refsect1-function.ssh2-auth-password-returnvalues">  <h3 class="title">返回值</h3>  <p class="para">   成功时返回 <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.true">true</a></span></strong>， 或者在失败时返回 <strong><span><a href="https://php.net/manual/zh/reserved.constants.php#constant.false">false</a></span></strong>。  </p> </div> <div class="refsect1 examples" id="refsect1-function.ssh2-auth-password-examples">  <h3 class="title">示例</h3>  <p class="para">   </p><div class="example" id="example-4789">    <p><strong>示例 #1 Authenticating with a password</strong></p>    <div class="example-contents"><div class="phpcode" style="border-color:gray;background:#1E1F22;"><blockquote style="border:1px gray solid;white-space:pre-wrap"><span style="color: #000000"><span style="color: #9876AA">&lt;?php<br>\$connection </span><span style="color: #007700">= </span><span style="color: #9876AA">ssh2_connect</span><span style="color: #007700">(</span><span style="color: #DD0000">'shell.example.com'</span><span style="color: #007700">, </span><span style="color: #9876AA">22</span><span style="color: #007700">);<br><br>if (</span><span style="color: #9876AA">ssh2_auth_password</span><span style="color: #007700">(</span><span style="color: #9876AA">\$connection</span><span style="color: #007700">, </span><span style="color: #DD0000">'username'</span><span style="color: #007700">, </span><span style="color: #DD0000">'secret'</span><span style="color: #007700">)) {<br>  echo </span><span style="color: #DD0000">"Authentication Successful!\n"</span><span style="color: #007700">;<br>} else {<br>  die(</span><span style="color: #DD0000">'Authentication Failed...'</span><span style="color: #007700">);<br>}<br></span><span style="color: #9876AA">?&gt;</span></span></blockquote></div>    </div>   </div>   </div></div>
 * 
 * (PECL ssh2 &gt;= 0.9.0)<br/>
